mapper.insert:
INSERT INTO ai_conversation (
    id, user_id, title, model, message_count, 
    last_message_preview, is_pinned, is_archived, status,
    created_at, updated_at
) VALUES (
    #{id}, #{userId}, #{title}, #{model}, #{messageCount},
    #{lastMessagePreview}, #{isPinned}, #{isArchived}, #{status},
    #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT * FROM ai_conversation 
WHERE user_id = #{userId}

    AND status = #{status}

ORDER BY is_pinned DESC, updated_at DESC

mapper.update:
UPDATE ai_conversation
title = #{title}, model = #{model}, message_count = #{messageCount}, last_message_preview = #{lastMessagePreview}, is_pinned = #{isPinned}, is_archived = #{isArchived}, status = #{status}, updated_at = NOW()
WHERE id = #{id}
