mapper.insert:
INSERT INTO ai_message (
    id, conversation_id, role, content, token_count,
    tool_calls, recommendations, model, duration_ms, is_error,
    created_at
) VALUES (
    #{id}, #{conversationId}, #{role}, #{content}, #{tokenCount},
    #{toolCalls}, #{recommendations}, #{model}, #{durationMs}, #{isError},
    #{createdAt}
)

INSERT INTO ai_message (
    id, conversation_id, role, content, token_count,
    tool_calls, recommendations, model, duration_ms, is_error,
    created_at
) VALUES

    (
        #{msg.id}, #{msg.conversationId}, #{msg.role}, #{msg.content}, #{msg.tokenCount},
        #{msg.toolCalls}, #{msg.recommendations}, #{msg.model}, #{msg.durationMs}, #{msg.isError},
        #{msg.createdAt}
    )

mapper.select:
SELECT * FROM ai_message 
WHERE conversation_id = #{conversationId}
ORDER BY created_at ASC

SELECT * FROM (
    SELECT * FROM ai_message 
    WHERE conversation_id = #{conversationId}
    ORDER BY created_at DESC
    LIMIT #{limit}
) sub
ORDER BY created_at ASC
