mapper.insert:
INSERT INTO article_comment (
    id, article_id, user_id, content, reply_id, like_count, create_time, update_time
) VALUES (
    #{id}, #{articleId}, #{userId}, #{content}, #{replyId}, #{likeCount}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT * FROM article_comment
WHERE reply_id = #{commentId}
ORDER BY create_time ASC

mapper.update:
UPDATE article_comment
SET like_count = #{likeCount}
WHERE id = #{id}
