mapper.update:
UPDATE article
title = #{title}, summary = #{summary}, content_url = #{contentUrl}, cover_image = #{coverImage}, category_id = #{categoryId}, status = #{status}, is_top = #{isTop}, is_original = #{isOriginal}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM article
status = 1 AND audit_status = 1 AND user_id = #{userId} AND category_id = #{categoryId}
ORDER BY is_top DESC, publish_time DESC

mapper.update:
UPDATE article
SET view_count = view_count + 1
WHERE id = #{id}

mapper.select:
SELECT * FROM article
WHERE user_id = #{userId}
ORDER BY create_time DESC

mapper.update:
UPDATE article SET audit_status = #{auditStatus}, audit_reason = #{auditReason}, audit_time = NOW(), status = #{status},
publish_time = NOW(),
update_time = NOW() WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM article
WHERE category_id = #{categoryId}
AND status = #{status}
AND audit_status = #{auditStatus}
