mapper.insert:
INSERT INTO tutorial_chapter (
    id, tutorial_id, chapter_order, chapter_title,
    duration, is_free, status,
    create_time, update_time
) VALUES (
    #{id}, #{tutorialId}, #{chapterOrder}, #{chapterTitle},
    #{duration}, #{isFree}, #{status},
    #{createTime}, #{updateTime}
)

mapper.update:
UPDATE tutorial
SET chapter_count = (
    SELECT COUNT(*) FROM tutorial_chapter WHERE tutorial_id = #{tutorialId}
)
WHERE id = #{tutorialId}
