mapper.insert:
INSERT INTO tutorial_comment (
    id, tutorial_id, user_id, content, parent_id, like_count, create_time
) VALUES (
    #{id}, #{tutorialId}, #{userId}, #{content}, #{parentId}, #{likeCount}, #{createTime}
)

mapper.update:
UPDATE tutorial_comment
SET like_count = #{likeCount}
WHERE id = #{id}
