mapper.select:
SELECT id, title, description, cover_image as coverImage, category_id as categoryId, difficulty, instructor_name as instructorName, instructor_avatar as instructorAvatar, instructor_desc as instructorDesc, price, status, view_count as viewCount, like_count as likeCount, recommend_count as recommendCount, student_count as studentCount, rating, chapter_count as chapterCount, create_user as createUser, create_time as createTime, update_time as updateTime FROM tutorial
AND status = #{status}
AND category_id = #{categoryId}
ORDER BY create_time DESC
