mapper.update:
update coder_hub.user
username = #{username}, phone = #{phone}, email = #{email}, avatar = #{avatar}, user_level = #{userLevel}, status = #{status},
where id = #{id}

mapper.select:
select * from coder_hub.user

    
    role = 'USER'
    
        and username like concat('%', #{username}, '%')
    
    
        and phone like concat('%', #{phone}, '%')
    
    
        and email like concat('%', #{email}, '%')
    
    
        and account = #{account}
    
    
        and user_level = #{userLevel}
    
    
        and status = #{status}
    

order by create_time desc
